import attr

class BuggyValidator:
    def __call__(self, inst, attr, value):
        # This should raise a NameError
        undefined_variable

@given(st.integers())
@example(42)  # Explicit example to demonstrate the bug
def test_or_validator_should_not_mask_programming_errors(value):
    validator = attr.validators.or_(
        BuggyValidator(),
        attr.validators.instance_of(int)
    )

    @attr.define
    class TestClass:
        x: int = attr.field(validator=validator)

    # This should raise NameError but doesn't
    try:
        TestClass(value)
        print(f"Test passed with value {value} - NameError was swallowed!")
    except NameError as e:
        print(f"NameError raised as expected: {e}")
        raise

# Run the test
test_or_validator_should_not_mask_programming_errors()

