import os
from hypothesis import given, strategies as st, example
from flask.helpers import get_debug_flag, get_load_dotenv

@given(
    st.sampled_from(["0", "false", "no"]),
    st.text(alphabet=" \t", min_size=1, max_size=5),
    st.text(alphabet=" \t", min_size=1, max_size=5)
)
@example("false", " ", " ")
def test_get_debug_flag_should_strip_whitespace(falsy_value, prefix, suffix):
    value_with_whitespace = prefix + falsy_value + suffix

    original_val = os.environ.get("FLASK_DEBUG")
    try:
        os.environ["FLASK_DEBUG"] = value_with_whitespace
        result = get_debug_flag()

        assert result is False, (
            f"FLASK_DEBUG={value_with_whitespace!r} should disable debug mode, "
            f"but got {result}"
        )
    finally:
        if original_val is None:
            os.environ.pop("FLASK_DEBUG", None)
        else:
            os.environ["FLASK_DEBUG"] = original_val

@given(
    st.sampled_from(["0", "false", "no"]),
    st.text(alphabet=" \t", min_size=1, max_size=5),
    st.text(alphabet=" \t", min_size=1, max_size=5)
)
@example("false", " ", " ")
def test_get_load_dotenv_should_strip_whitespace(falsy_value, prefix, suffix):
    value_with_whitespace = prefix + falsy_value + suffix

    original_val = os.environ.get("FLASK_SKIP_DOTENV")
    try:
        os.environ["FLASK_SKIP_DOTENV"] = value_with_whitespace
        result = get_load_dotenv()

        assert result is True, (
            f"FLASK_SKIP_DOTENV={value_with_whitespace!r} should enable dotenv loading, "
            f"but got {result}"
        )
    finally:
        if original_val is None:
            os.environ.pop("FLASK_SKIP_DOTENV", None)
        else:
            os.environ["FLASK_SKIP_DOTENV"] = original_val

if __name__ == "__main__":
    print("Running property-based tests for Flask environment variable handling...")
    print("=" * 60)

    print("\nTesting get_debug_flag():")
    print("-" * 40)
    test_get_debug_flag_should_strip_whitespace()

    print("\nTesting get_load_dotenv():")
    print("-" * 40)
    test_get_load_dotenv_should_strip_whitespace()
