from hypothesis import given, strategies as st, assume, settings
import pandas as pd
import math

@given(st.lists(st.floats(-100, 100, allow_nan=False), min_size=1, max_size=50),
       st.integers(2, 10))
@settings(max_examples=100)
def test_cut_coverage(data, n_bins):
    assume(len(set(data)) > 1)
    result = pd.cut(data, bins=n_bins)
    assert len(result) == len(data)

# Run the test and capture failures
if __name__ == "__main__":
    try:
        test_cut_coverage()
        print("All tests passed!")
    except AssertionError as e:
        print(f"Test failed with assertion: {e}")
    except Exception as e:
        print(f"Test failed with error: {e}")
        import traceback
        traceback.print_exc()
