#!/usr/bin/env python3
"""
Hypothesis-based property test for scipy.datasets.clear_cache
This test demonstrates that the function silently accepts invalid inputs
when the cache directory doesn't exist.
"""

from hypothesis import given, strategies as st
import pytest
import scipy.datasets
import os
import shutil

# Ensure cache directory doesn't exist for consistent test conditions
cache_dir = os.path.expanduser("~/.cache/scipy-data")
if os.path.exists(cache_dir):
    shutil.rmtree(cache_dir)

@given(st.text(min_size=1))
def test_clear_cache_rejects_arbitrary_strings(text_input):
    """Property: clear_cache should reject any string input"""
    if callable(text_input):
        return

    with pytest.raises((ValueError, TypeError, AssertionError)):
        scipy.datasets.clear_cache(text_input)

@given(st.integers())
def test_clear_cache_rejects_integers(int_input):
    """Property: clear_cache should reject integer inputs"""
    with pytest.raises((ValueError, TypeError, AssertionError)):
        scipy.datasets.clear_cache(int_input)

@given(st.dictionaries(st.text(), st.text()))
def test_clear_cache_rejects_dictionaries(dict_input):
    """Property: clear_cache should reject dictionary inputs"""
    with pytest.raises((ValueError, TypeError, AssertionError)):
        scipy.datasets.clear_cache(dict_input)

if __name__ == "__main__":
    # Run the tests directly without pytest
    print("Running Hypothesis tests for scipy.datasets.clear_cache")
    print("=" * 60)

    # Test with a few string examples
    print("\nTesting with string inputs:")
    test_strings = ['0', 'invalid_string', 'test', '123', 'None']
    for test_str in test_strings:
        try:
            test_clear_cache_rejects_arbitrary_strings(test_str)
            print(f"  '{test_str}': Test failed - no error was raised!")
        except AssertionError as e:
            print(f"  '{test_str}': Test FAILED - expected error but got none!")
        except Exception as e:
            print(f"  '{test_str}': Test passed - error was raised")

    # Test with integer examples
    print("\nTesting with integer inputs:")
    test_ints = [0, 42, -1, 999999]
    for test_int in test_ints:
        try:
            test_clear_cache_rejects_integers(test_int)
            print(f"  {test_int}: Test failed - no error was raised!")
        except AssertionError as e:
            print(f"  {test_int}: Test FAILED - expected error but got none!")
        except Exception as e:
            print(f"  {test_int}: Test passed - error was raised")

    # Test with dictionary examples
    print("\nTesting with dictionary inputs:")
    test_dicts = [{'key': 'value'}, {}, {'a': 1, 'b': 2}]
    for test_dict in test_dicts:
        try:
            test_clear_cache_rejects_dictionaries(test_dict)
            print(f"  {test_dict}: Test failed - no error was raised!")
        except AssertionError as e:
            print(f"  {test_dict}: Test FAILED - expected error but got none!")
        except Exception as e:
            print(f"  {test_dict}: Test passed - error was raised")

    print("\n" + "=" * 60)
    print("SUMMARY: All tests FAIL when cache directory doesn't exist!")
    print("This confirms the bug: invalid inputs are silently accepted.")
