#!/usr/bin/env python3

from hypothesis import given, strategies as st, settings
from Cython.Utils import normalise_float_repr

failed_cases = []
total_tests = 0

@given(st.floats(allow_nan=False, allow_infinity=False, min_value=-1e10, max_value=1e10))
@settings(max_examples=100)
def test_normalise_float_repr_round_trip(x):
    global failed_cases, total_tests
    total_tests += 1
    float_str = str(x)
    try:
        normalized = normalise_float_repr(float_str)
        normalized_value = float(normalized)
        original_value = float(float_str)
        if abs(normalized_value - original_value) > 1e-15:  # Allow for tiny float precision errors
            failed_cases.append((float_str, normalized, original_value, normalized_value))
    except Exception as e:
        # Record exceptions too
        failed_cases.append((float_str, f"ERROR: {e}", float(float_str), None))

# Run the test
try:
    test_normalise_float_repr_round_trip()
except:
    pass  # Continue even if hypothesis finds failures

print(f"\n=== Hypothesis Test Results ===")
print(f"Total tests run: {total_tests}")
print(f"Failed cases: {len(failed_cases)}")

if failed_cases:
    print(f"\nShowing up to 10 failures:")
    for i, failure in enumerate(failed_cases[:10]):
        if len(failure) == 4:
            orig, norm, orig_val, norm_val = failure
            if norm_val is not None:
                ratio = norm_val / orig_val if orig_val != 0 else "undefined"
                print(f"  {orig} -> {norm}")
                print(f"    Expected: {orig_val}, Got: {norm_val}, Ratio: {ratio}")
            else:
                print(f"  {orig} -> {norm}")
else:
    print("All tests passed!")
