#!/usr/bin/env python3
"""Simple reproduction test for is_re_compilable bug"""

from pandas.core.dtypes.inference import is_re_compilable

print("Testing is_re_compilable with invalid regex pattern '('")
try:
    result = is_re_compilable('(')
    print(f"Result: {result}")
except Exception as e:
    print(f"Exception raised: {type(e).__name__}: {e}")

print("\nTesting with other invalid patterns:")
invalid_patterns = [')', '[', '*', '+', '?']
for pattern in invalid_patterns:
    print(f"  Pattern '{pattern}':", end=" ")
    try:
        result = is_re_compilable(pattern)
        print(f"Result: {result}")
    except Exception as e:
        print(f"Exception: {type(e).__name__}")

print("\nTesting with valid patterns:")
valid_patterns = ['abc', 'a.*b', '[abc]', '(abc)', 'a+b']
for pattern in valid_patterns:
    print(f"  Pattern '{pattern}':", end=" ")
    try:
        result = is_re_compilable(pattern)
        print(f"Result: {result}")
    except Exception as e:
        print(f"Exception: {type(e).__name__}")

