from hypothesis import given, strategies as st, settings
from Cython.Plex.Regexps import Range
from Cython.Plex.Errors import PlexValueError
import pytest

@given(st.text(min_size=1).filter(lambda s: len(s) % 2 == 1))
@settings(max_examples=200)
def test_range_validates_even_length(s):
    with pytest.raises(PlexValueError):
        Range(s)

# Test with the specific failing input
if __name__ == "__main__":
    try:
        test_range_validates_even_length()
        print("Test passed - but it shouldn't have!")
    except Exception as e:
        print(f"Test failed as expected: {e}")

    # Also test the specific example
    print("\nTesting with '000':")
    try:
        Range('000')
        print("No error raised")
    except IndexError as e:
        print(f"IndexError raised: {e}")
    except PlexValueError as e:
        print(f"PlexValueError raised: {e}")

