#!/usr/bin/env python
import os
import sys
import django
from django.conf import settings

# Configure Django
if not settings.configured:
    settings.configure(
        DEBUG=True,
        DATABASES={
            'default': {
                'ENGINE': 'django.db.backends.sqlite3',
                'NAME': ':memory:',
            }
        },
        INSTALLED_APPS=[
            'django.contrib.contenttypes',
            'django.contrib.auth',
        ],
        SECRET_KEY='test-secret-key',
    )
    django.setup()

# Now run the test
from django.core import serializers
from django.core.serializers.base import DeserializationError
import json
from hypothesis import given, strategies as st

print("Testing the bug report...")
print("=" * 60)

# First, reproduce the simple example
print("\n1. Testing simple example with missing 'model' key:")
print("-" * 40)
try:
    list(serializers.deserialize('json', '[{"fields": {}}]'))
    print("ERROR: No exception was raised!")
except KeyError as e:
    print(f"✓ KeyError raised: {e}")
except DeserializationError as e:
    print(f"✗ DeserializationError raised: {e}")
except Exception as e:
    print(f"✗ Unexpected exception: {type(e).__name__}: {e}")

# Test with missing 'fields' key
print("\n2. Testing with missing 'fields' key:")
print("-" * 40)
try:
    list(serializers.deserialize('json', '[{"model": "auth.User"}]'))
    print("ERROR: No exception was raised!")
except KeyError as e:
    print(f"✓ KeyError raised: {e}")
except DeserializationError as e:
    print(f"✗ DeserializationError raised: {e}")
except Exception as e:
    print(f"✗ Unexpected exception: {type(e).__name__}: {e}")

# Run property-based test
print("\n3. Running property-based test:")
print("-" * 40)

test_passed = True
test_errors = []

@given(st.dictionaries(st.text(), st.text()).filter(lambda d: 'model' not in d or 'fields' not in d))
def test_deserializer_handles_malformed_json(malformed_dict):
    global test_passed, test_errors
    json_str = json.dumps([malformed_dict])
    try:
        list(serializers.deserialize('json', json_str))
    except DeserializationError:
        # This is what we expect
        pass
    except KeyError as e:
        test_passed = False
        test_errors.append(f"KeyError with input {malformed_dict}: {e}")
    except Exception as e:
        if not isinstance(e, DeserializationError):
            test_passed = False
            test_errors.append(f"Unexpected {type(e).__name__} with input {malformed_dict}: {e}")

# Run the property test with a few examples
try:
    test_deserializer_handles_malformed_json()
    if test_passed:
        print("✗ Property test passed (but shouldn't have - KeyError should be raised)")
    else:
        print("✓ Property test failed as expected")
        for error in test_errors[:5]:  # Show first 5 errors
            print(f"  - {error}")
except Exception as e:
    print(f"✓ Property test raised exception as expected: {type(e).__name__}")

print("\n" + "=" * 60)
print("Bug confirmed: Django raises KeyError instead of DeserializationError")
print("for malformed JSON missing 'model' or 'fields' keys.")
