#!/usr/bin/env python3
"""Test script to reproduce the AppConfig.create() bug with trailing dot"""

import os
import sys
import django
from django.conf import settings

# Configure Django settings
if not settings.configured:
    settings.configure(
        DEBUG=True,
        INSTALLED_APPS=[
            'django.contrib.auth',
            'django.contrib.contenttypes',
        ],
        DATABASES={
            'default': {
                'ENGINE': 'django.db.backends.sqlite3',
                'NAME': ':memory:',
            }
        }
    )
    django.setup()

from django.apps.config import AppConfig

print("Testing AppConfig.create() with trailing dot...")
print("=" * 60)

# Test 1: Normal case (should work)
print("\nTest 1: Normal app entry 'django.contrib.auth'")
try:
    config = AppConfig.create('django.contrib.auth')
    print(f"✓ Success: {config}")
except Exception as e:
    print(f"✗ Failed: {type(e).__name__}: {e}")

