try:
    config = AppConfig.create('django.contrib.auth.')
    print(f"✓ Success: {config}")
except IndexError as e:
    print(f"✗ IndexError raised: {e}")
    import traceback
    traceback.print_exc()
except Exception as e:
    print(f"✗ Other exception: {type(e).__name__}: {e}")

# Test 3: Multiple trailing dots
print("\nTest 3: Entry with multiple trailing dots 'django.contrib.auth..'")
try:
    config = AppConfig.create('django.contrib.auth..')
    print(f"✓ Success: {config}")
except IndexError as e:
    print(f"✗ IndexError raised: {e}")
except Exception as e:
    print(f"✗ Other exception: {type(e).__name__}: {e}")

# Test 4: Just a dot
print("\nTest 4: Just a dot '.'")
try:
    config = AppConfig.create('.')
    print(f"✓ Success: {config}")
except IndexError as e:
    print(f"✗ IndexError raised: {e}")
except Exception as e:
    print(f"✗ Other exception: {type(e).__name__}: {e}")

print("\n" + "=" * 60)
print("Test completed")

#!/usr/bin/env python3
"""Hypothesis property-based test for AppConfig.create() bug"""

import os
import sys
import django
from django.conf import settings

# Configure Django settings
if not settings.configured:
    settings.configure(
        DEBUG=True,
        INSTALLED_APPS=[
            'django.contrib.auth',
            'django.contrib.contenttypes',
        ],
        DATABASES={
            'default': {
                'ENGINE': 'django.db.backends.sqlite3',
                'NAME': ':memory:',
            }
        }
    )
    django.setup()

from hypothesis import given, strategies as st, settings as hyp_settings
from django.apps.config import AppConfig

# The property-based test from the bug report
@given(st.text(min_size=1).filter(lambda s: '.' in s))
@hyp_settings(max_examples=100, deadline=None)
def test_create_with_module_paths(entry):
    """Test AppConfig.create() with various module paths"""
    if entry.endswith('.') and not entry.rpartition('.')[2]:
        # This is the bug condition - should raise an exception
        # But we check what kind of exception
        try:
            result = AppConfig.create(entry)
            print(f"UNEXPECTED SUCCESS for '{entry}': {result}")
            assert False, f"Should raise an exception for entry '{entry}'"
        except IndexError as e:
            # This is the bug - IndexError is raised
            print(f"IndexError for '{entry}': {e}")
        except (ImportError, Exception) as e:
            # Other exceptions are acceptable
            print(f"Other exception for '{entry}': {type(e).__name__}: {e}")
    else:
        # For other entries, we just check it doesn't crash with IndexError
        try:
            result = AppConfig.create(entry)
            print(f"Success for '{entry}': {result}")
        except IndexError as e:
            print(f"UNEXPECTED IndexError for '{entry}': {e}")
            # IndexError should only happen for the specific bug case
            assert False, f"Unexpected IndexError for entry '{entry}'"
        except Exception as e:
            # Other exceptions are fine for invalid module names
            pass

# Run the test
print("Running Hypothesis property-based test...")
print("=" * 60)

# Run specific examples first
specific_examples = [
    "django.contrib.auth.",
    "test.",
    "a.b.c.",
    ".",
    "..",
    "module.submodule.",
]

print("\nTesting specific examples:")
for example in specific_examples:
    print(f"\nTesting: '{example}'")
    try:
        test_create_with_module_paths(example)
    except AssertionError as e:
        print(f"Assertion failed: {e}")
    except Exception as e:
        print(f"Unexpected error: {e}")

print("\n" + "=" * 60)
print("\nRunning property-based testing with random inputs...")
try:
    test_create_with_module_paths()
    print("\nProperty-based testing completed successfully!")
except Exception as e:
    print(f"\nProperty-based testing failed: {e}")
