from hypothesis import given, settings, strategies as st
import pytest
from Cython.Plex import Range


@settings(max_examples=100)
@given(st.text(min_size=1, max_size=20))
def test_range_validates_input_properly(s):
    if len(s) % 2 == 0:
        re = Range(s)
        assert hasattr(re, 'nullable')
    else:
        with pytest.raises(ValueError, match="even length"):
            Range(s)

# Run the test manually
if __name__ == "__main__":
    # Test with even-length strings
    for test_str in ['', 'ab', 'abcd', '123456']:
        try:
            re = Range(test_str)
            print(f"✓ Range('{test_str}') works - has nullable: {hasattr(re, 'nullable')}")
        except Exception as e:
            print(f"✗ Range('{test_str}') failed: {e}")

    # Test with odd-length strings
    for test_str in ['a', 'abc', '12345']:
        try:
            re = Range(test_str)
            print(f"✗ Range('{test_str}') should have raised ValueError but didn't")
        except IndexError as e:
            print(f"✗ Range('{test_str}') raised IndexError: {e}")
        except ValueError as e:
            print(f"✓ Range('{test_str}') raised ValueError: {e}")
