#!/usr/bin/env python3
"""Run hypothesis test from bug report"""

from hypothesis import given, strategies as st, settings
import pandas.io.json as json_module

# Store failures for reporting
failures = []

@given(st.integers())
@settings(max_examples=1000)
def test_ujson_int_round_trip(n):
    """
    Property: All Python integers should round-trip through ujson
    """
    try:
        json_str = json_module.ujson_dumps(n)
        recovered = json_module.ujson_loads(json_str)
        if n != recovered:
            failures.append((n, json_str, recovered))
            print(f"FAIL: {n} -> {json_str} -> {recovered}")
    except Exception as e:
        # Exceptions are acceptable (e.g. "Value is too big")
        # Silent corruption is not
        pass

# Run the test
print("Running hypothesis test for ujson round-trip property...")
print("=" * 60)

try:
    test_ujson_int_round_trip()
    print("\nTest completed.")
except AssertionError:
    print("\nAssertion failed during testing.")

# Report failures
if failures:
    print(f"\nFound {len(failures)} silent corruption cases:")
    for orig, json_str, recovered in failures[:10]:  # Show first 10
        print(f"  {orig} -> {json_str} -> {recovered}")
    if len(failures) > 10:
        print(f"  ... and {len(failures) - 10} more")
else:
    print("\nNo silent corruption found in samples tested.")

#!/usr/bin/env python3
"""Test specific problematic values"""

from hypothesis import given, strategies as st, example, settings
import pandas.io.json as json_module

@given(st.integers())
@example(-18446744073709551616)  # -2^64 - the specific failing case
@example(-18446744073709551617)  # -2^64 - 1
@settings(max_examples=10)
def test_ujson_with_examples(n):
    """Test with specific examples"""
    print(f"\nTesting n = {n}")
    try:
        json_str = json_module.ujson_dumps(n)
        print(f"  Dumps: {json_str}")
        recovered = json_module.ujson_loads(json_str)
        print(f"  Loads: {recovered}")
        assert n == recovered, f"Round-trip failed: {n} != {recovered}"
        print(f"  ✓ Round-trip successful")
    except AssertionError as e:
        print(f"  ✗ ASSERTION FAILED: {e}")
        raise
    except Exception as e:
        print(f"  Exception (acceptable): {e}")

print("Testing hypothesis with specific examples...")
print("=" * 60)

try:
    test_ujson_with_examples()
except AssertionError as e:
    print(f"\nTest failed with assertion error: {e}")
