from hypothesis import given, strategies as st, settings, example
import numpy as np
from pandas.core.indexers import length_of_indexer

# First let's run the property-based test
@given(
    n=st.integers(min_value=1, max_value=1000),
    start=st.integers(min_value=-100, max_value=100) | st.none(),
    stop=st.integers(min_value=-100, max_value=100) | st.none(),
    step=st.integers(min_value=-10, max_value=10).filter(lambda x: x != 0) | st.none(),
)
@settings(max_examples=500)
@example(n=1, start=None, stop=None, step=-1)
@example(n=1, start=2, stop=None, step=None)
@example(n=1, start=None, stop=-5, step=None)
def test_length_of_indexer_matches_actual(n, start, stop, step):
    target = np.arange(n)
    indexer = slice(start, stop, step)

    expected_len = length_of_indexer(indexer, target)
    actual_len = len(target[indexer])

    print(f"n={n}, start={start}, stop={stop}, step={step}: expected={expected_len}, actual={actual_len}")

    assert expected_len == actual_len, f"Mismatch for n={n}, start={start}, stop={stop}, step={step}: expected={expected_len}, actual={actual_len}"

# Run the test
if __name__ == "__main__":
    print("Running property-based test...")
    test_length_of_indexer_matches_actual()
    print("\nTest passed for all examples!")
