#!/usr/bin/env python3
"""Test the hypothesis property test from the bug report."""

from hypothesis import given, strategies as st, settings
import pandas.io.clipboard as clipboard

@settings(max_examples=100)
@given(st.text())
def test_copy_paste_roundtrip(text):
    """Test that copy-paste roundtrips correctly."""
    if not clipboard.is_available():
        print("Clipboard not available, skipping test")
        return

    clipboard.copy(text)
    result = clipboard.paste()
    assert result == text, f"Expected {text!r}, got {result!r}"

if __name__ == "__main__":
    print("Testing clipboard roundtrip with Hypothesis...")
    try:
        # Test with a simple string first
        test_text = "Hello, World!"
        print(f"Testing with: {test_text!r}")

        if clipboard.is_available():
            clipboard.copy(test_text)
            result = clipboard.paste()
            print(f"Result: {result!r}")
            print(f"Match: {result == test_text}")
        else:
            print("Clipboard not available on this system")

        # Now run the hypothesis test
        print("\nRunning hypothesis tests...")
        test_copy_paste_roundtrip()
        print("All tests passed!")
    except Exception as e:
        print(f"Error: {e}")
