#!/usr/bin/env python3
"""Property-based test for ODR.set_iprint bug using Hypothesis."""

from hypothesis import given, strategies as st, settings
from scipy.odr import ODR, Data, Model
import traceback

def linear_func(B, x):
    return B[0] * x + B[1]

@given(st.integers(min_value=0, max_value=9999))
@settings(max_examples=200)
def test_set_iprint_robust_to_manual_iprint(manual_iprint):
    data = Data([1, 2, 3], [2, 4, 6])
    model = Model(linear_func)
    odr = ODR(data, model, beta0=[1, 1])

    odr.iprint = manual_iprint

    try:
        odr.set_iprint(final=0)
    except (IndexError, ValueError) as e:
        # These are the bugs we're testing for
        print(f"Failed on iprint={manual_iprint}: {type(e).__name__}: {e}")
        raise

# Run the test
print("Running property-based test with 200 examples...")
try:
    test_set_iprint_robust_to_manual_iprint()
    print("Test completed - no failures found!")
except Exception as e:
    print(f"\nTest failed! See output above for failing inputs.")
    print(f"Final error: {type(e).__name__}")
