from hypothesis import given, strategies as st
import numpy as np
import scipy.signal.windows as windows


@given(st.integers(min_value=1, max_value=100),
       st.floats(min_value=-100, max_value=100, allow_nan=False))
def test_gaussian_no_nan(M, std):
    """Gaussian window should never produce NaN for finite std values."""
    if std <= 0:
        # Either raise an error or produce valid output
        try:
            w = windows.gaussian(M, std)
            assert not np.any(np.isnan(w)), \
                f"gaussian({M}, std={std}) produced NaN"
        except (ValueError, ZeroDivisionError):
            pass
    else:
        w = windows.gaussian(M, std)
        assert not np.any(np.isnan(w))

# Run the test
if __name__ == "__main__":
    # Test the specific failing case
    print("Testing specific case: M=3, std=0.0")
    try:
        test_gaussian_no_nan(3, 0.0)
        print("Test passed for M=3, std=0.0")
    except AssertionError as e:
        print(f"Test failed: {e}")

    # Run property-based testing
    print("\nRunning property-based tests...")
    test_gaussian_no_nan()

