from hypothesis import given, strategies as st, settings, example
from scipy.io.arff._arffread import DateAttribute
import pytest

@given(st.text(
    alphabet=st.characters(
        blacklist_characters='yMdHmsYzZ0123456789',
        blacklist_categories=('Cs', 'Cc')
    ),
    min_size=1,
    max_size=30
).filter(lambda s: s.strip()))
@settings(max_examples=50)  # Reduced from 500 for quicker testing
@example("foobar")
@example("xyz")
@example("invalid")
def test_invalid_date_format_rejected(invalid_pattern):
    """
    Property: Date patterns without any valid date components should raise ValueError.
    Valid components are: yyyy, yy, MM, dd, HH, mm, ss
    """
    attr_string = f'date "{invalid_pattern}"'

    try:
        date_format, datetime_unit = DateAttribute._get_date_format(attr_string)
        print(f"FAIL: Pattern '{invalid_pattern}' accepted with format='{date_format}', unit='{datetime_unit}'")
        assert False, f"Pattern '{invalid_pattern}' should have raised ValueError"
    except ValueError as e:
        # Check if it's the timezone error (contains 'z' or 'Z')
        if 'z' in invalid_pattern.lower() and "time zone" in str(e):
            print(f"OK: Pattern '{invalid_pattern}' rejected due to timezone: {e}")
        else:
            print(f"OK: Pattern '{invalid_pattern}' rejected: {e}")

if __name__ == "__main__":
    test_invalid_date_format_rejected()

