#!/usr/bin/env python3
"""Run the hypothesis test from the bug report"""

import numpy as np
import pytest
from hypothesis import given, strategies as st, example, settings
import scipy.odr as odr

failures = []
successes = []

@given(
    iprint=st.integers(min_value=0, max_value=9999),
)
@example(iprint=7)
@example(iprint=8)
@example(iprint=9)
@example(iprint=700)
@example(iprint=7000)
@settings(max_examples=50, deadline=None)
def test_set_iprint_with_various_initial_values(iprint):
    global failures, successes
    x = np.arange(10, dtype=float)
    y = np.arange(10, dtype=float)
    data = odr.Data(x, y)

    def fcn(beta, x):
        return beta[0] * x + beta[1]

    model = odr.Model(fcn)
    odr_obj = odr.ODR(data, model, beta0=[1.0, 1.0])

    odr_obj.iprint = iprint

    try:
        odr_obj.set_iprint(init=1)
        successes.append(iprint)
    except IndexError:
        failures.append(iprint)
        # Let hypothesis know this failed
        raise
    except Exception as e:
        print(f"Unexpected error for iprint={iprint}: {e}")
        raise

if __name__ == "__main__":
    print("Running hypothesis test...")
    try:
        test_set_iprint_with_various_initial_values()
    except Exception as e:
        print(f"Test failed as expected with: {type(e).__name__}: {e}")

    print(f"\nSummary:")
    print(f"  Failed with IndexError: {sorted(set(failures))[:20]}...")
    print(f"  Total failures: {len(failures)}")
    print(f"  Succeeded: {sorted(set(successes))[:20]}...")
    print(f"  Total successes: {len(successes)}")
