import numpy as np
import scipy.fft

print("Testing DCT (forward) with single element:")
x = np.array([1.0])
print(f"Input array: {x}")

for dct_type in [1, 2, 3, 4]:
    try:
        result = scipy.fft.dct(x, type=dct_type)
        print(f"DCT Type {dct_type}: Success - Result = {result}")
    except Exception as e:
        print(f"DCT Type {dct_type}: Error - {type(e).__name__}: {e}")

print("\nTesting IDCT (inverse) with single element:")
for dct_type in [1, 2, 3, 4]:
    try:
        result = scipy.fft.idct(x, type=dct_type)
        print(f"IDCT Type {dct_type}: Success - Result = {result}")
    except Exception as e:
        print(f"IDCT Type {dct_type}: Error - {type(e).__name__}: {e}")

print("\nTesting roundtrip for types that work:")
for dct_type in [2, 3, 4]:
    try:
        dct_result = scipy.fft.dct(x, type=dct_type)
        idct_result = scipy.fft.idct(dct_result, type=dct_type)
        print(f"Type {dct_type} roundtrip: {x} -> {dct_result} -> {idct_result}")
        assert np.allclose(idct_result, x), f"Roundtrip failed for type {dct_type}"
        print(f"  Roundtrip successful!")
    except Exception as e:
        print(f"Type {dct_type} roundtrip error: {e}")
