#!/usr/bin/env python3
"""Run the hypothesis test from the bug report"""

from hypothesis import given, strategies as st, settings
from scipy.odr._odrpack import _report_error

@given(st.integers(min_value=0, max_value=99999))
@settings(max_examples=1000)
def test_report_error_returns_nonempty_list(info):
    result = _report_error(info)
    assert isinstance(result, list), f"Expected list, got {type(result)}"
    assert len(result) > 0, f"Expected non-empty list for info={info}, got {result}"

# Run the test
print("Running hypothesis test...")
try:
    test_report_error_returns_nonempty_list()
    print("Test passed for all examples!")
except AssertionError as e:
    print(f"Test failed: {e}")
except Exception as e:
    print(f"Error during test: {e}")

# Also test the specific failing case mentioned
print("\nTesting specific failing case info=70000:")
try:
    result = _report_error(70000)
    assert isinstance(result, list), f"Expected list, got {type(result)}"
    assert len(result) > 0, f"Expected non-empty list for info=70000, got {result}"
    print("Test passed!")
except AssertionError as e:
    print(f"Test failed: {e}")
