        print(f"FAILURE: ZeroDivisionError for index of length {len(index.index)} with shift {shift}")
        print(f"Error: {e}")
        raise
    except Exception as e:
        print(f"UNEXPECTED ERROR: {type(e).__name__}: {e}")
        raise

if __name__ == "__main__":
    test_pandasindex_roll_no_crash()

