#!/usr/bin/env python3
"""Test script to reproduce the VAR_POSITIONAL bug in _deprecate_positional_args"""

import sys
sys.path.insert(0, '/home/npc/pbt/agentic-pbt/envs/xarray_env/lib/python3.13/site-packages')

from xarray.util.deprecation_helpers import _deprecate_positional_args
import warnings
import traceback

print("Testing _deprecate_positional_args with VAR_POSITIONAL (*args) function")
print("=" * 70)

# Test 1: Simple reproduction
print("\n1. Simple reproduction test:")
try:
    @_deprecate_positional_args("v0.1.0")
    def func(*args, **kwargs):
        return sum(args) + sum(kwargs.values())

    with warnings.catch_warnings():
        warnings.simplefilter("ignore")
        result = func(1, 2, a=3)
        print(f"   Result: {result}")
        print("   SUCCESS - No error occurred (unexpected)")
except Exception as e:
    print(f"   ERROR: {type(e).__name__}: {e}")
    traceback.print_exc()

# Test 2: Function with no *args (expected to work)
print("\n2. Control test - function without *args:")
try:
    @_deprecate_positional_args("v0.1.0")
    def func_no_varargs(x, y, *, z=0):
        return x + y + z

    with warnings.catch_warnings(record=True) as w:
        warnings.simplefilter("always")
        result = func_no_varargs(1, 2, z=3)
        print(f"   Result: {result}")
        if w:
            print(f"   Warnings: {[str(warning.message) for warning in w]}")
        else:
            print("   No warnings (as expected with keyword args)")
except Exception as e:
    print(f"   ERROR: {type(e).__name__}: {e}")

# Test 3: Try calling with positional args on normal function
print("\n3. Test deprecation warning on normal function:")
try:
    @_deprecate_positional_args("v0.1.0")
    def func_normal(x, *, y=0, z=0):
        return x + y + z

    with warnings.catch_warnings(record=True) as w:
        warnings.simplefilter("always")
        result = func_normal(1, 2, 3)  # Should issue warning and convert to kwargs
        print(f"   Result: {result}")
        if w:
            for warning in w:
                print(f"   Warning: {warning.message}")
        else:
            print("   No warnings (unexpected)")
except Exception as e:
    print(f"   ERROR: {type(e).__name__}: {e}")

print("\n" + "=" * 70)
print("Summary: The decorator crashes with ValueError when applied to functions with *args")

#!/usr/bin/env python3
"""Hypothesis test for the VAR_POSITIONAL bug in _deprecate_positional_args"""

import sys
sys.path.insert(0, '/home/npc/pbt/agentic-pbt/envs/xarray_env/lib/python3.13/site-packages')

from hypothesis import given, strategies as st, settings
from xarray.util.deprecation_helpers import _deprecate_positional_args
import warnings

print("Running Hypothesis test for VAR_POSITIONAL bug")
print("=" * 70)

test_passed = True
errors = []

@given(st.integers(), st.integers(), st.integers())
@settings(max_examples=10)
def test_decorator_with_varargs(x, y, z):
    global test_passed, errors

    @_deprecate_positional_args("v0.1.0")
    def func(*args, **kwargs):
        return sum(args) + sum(kwargs.values())

    try:
        with warnings.catch_warnings():
            warnings.simplefilter("ignore")
            result = func(x, y, a=z)
            print(f"Test with x={x}, y={y}, z={z}: Result={result}")
    except ValueError as e:
        test_passed = False
        errors.append(f"Failed with x={x}, y={y}, z={z}: {e}")
        print(f"FAILED with x={x}, y={y}, z={z}: {e}")
        raise

try:
    test_decorator_with_varargs()
except Exception as e:
    print(f"\nHypothesis test failed with error: {e}")

print("\n" + "=" * 70)
if test_passed:
    print("All hypothesis tests passed (unexpected)")
else:
    print(f"Hypothesis tests failed with {len(errors)} errors")
    for error in errors[:5]:  # Show first 5 errors
        print(f"  - {error}")
